import React from 'react';
import './App.css';
import Tooltip from './Tooltip';

class App extends React.Component{

  constructor(){
    super();
    this.state={
      pos:"top",
      hover:false,
    };
  }
  //handleClick function which handle the pos
  handleClick=(pos)=>{
    this.setState({
      pos:pos,
    })
  };


  //handleMouseEnter function which handle the hover condintion


  handleMouseEnter=()=>{
    this.setState({
      hover:true,
    });
  };



    //handleMouseLeave function which handle the hover condintion


  handleMouseLeave=()=>{
    this.setState({
      hover:false
    })
  }




  render(){
    return (


      <div className="App">
{  //pos of the all button
}
        <div className='btn-pos'>
          <div className='grp-name'>
            Select the pos of the hover!
          </div>
          <button
          className= {this.state.pos=== 'top' ? "btn active" :"btn" }
          onClick={(e)=>{
            this.handleClick('top');
          }}>
            top
          </button>

          <button
          className={this.state.pos=== 'right' ? "btn active" :"btn"}
          onClick={(e)=>{
            this.handleClick('right');
          }}>
            right
          </button>


          <button
          className={this.state.pos=== 'left' ? "btn active" :"btn"}
          onClick={(e)=>{
            this.handleClick('left');
          }}>
            left
          </button>

          <button
          className={this.state.pos=== 'down' ? "btn active" :"btn"}
          onClick={(e)=>{
            this.handleClick('down');
          }}>
            down
          </button>
        </div>
{        //buttons

}        <div id="button-container">
          <button
          className='btn hover-btn'
          onMouseOver={this.handleMouseEnter}
          onMouseOut={this.handleMouseLeave}
          >

            Hover Over Me!!
          </button>
          {this.state.hover && <Tooltip pos={this.state.pos}/>}
        </div>


      </div>
    );
  }
}

export default App;
